mapper.update:
update t_sys_goodsinfo set
goods_status = #{goodsStateId},
inventory = #{goodsInventory}, sales = sales + #{goodsSales}, last_modified_by = #{userId}, gmt_modified = now(), version = version + 1 where is_deleted = 0 and goods_id = #{goodsId}

mapper.select:
select a.order_id, a.order_total_price, a.order_status, a.goods_all_cnt, a.version from t_sys_order a where a.is_deleted = 0 and order_cus_id = #{userId}
AND a.order_status = #{orderStateId}
order by a.gmt_create desc
