mapper.select:
select count(sort_id)
from t_sys_carousel_figure
where is_deleted = 0
and sort_id = #{sortId}

    and picture_id != #{pictureId}

select sort_id sortId, picture_path picturePath, time_start timeStart, time_end timeEnd, picture_status pictureStatus FROM t_sys_carousel_figure where is_deleted = 0
and picture_status = #{pictureStatus}
order by sort_id asc

update.foreach:
update t_sys_carousel_figure
picture_status = #{item.pictureStatus}, gmt_modified = now(), last_modified_by = #{item.lastModifiedBy,jdbcType=VARCHAR}, version = version + 1
picture_id = #{item.pictureId,jdbcType=VARCHAR} AND version = #{item.version}
