mapper.select:
select a.order_id orderId, a.order_cus_id orderCusId, b.user_name orderCusName, a.order_total_price orderPrice, a.order_status orderStatus, b.phone orderCusPhone, a.store_id storeId, a.pay_time payTime, a.version FROM t_sys_order a, t_sys_user b where a.is_deleted = 0 and b.is_deleted = 0 and a.order_cus_id = b.user_id
and a.order_cus_name like concat('%', #{orderCusName}, '%')
and a.order_id like concat('%', #{orderId}, '%')
and a.order_cus_phone like concat('%', #{orderCusPhone}, '%')
and a.order_status = #{orderStatus}
and a.pay_time between #{payTimeStart} and #{payTimeEnd}
and a.pay_time <= #{payTimeEnd}
and a.pay_time >= #{payTimeStart}
and a.store_id in (select store_id from t_sys_store where manager_id = #{loginUserId})
order by a.gmt_create desc
